mapper.select:
select id, fullname, email, mphone
from tbl_contacts
where customer_id = #{customerId}

select co.id, u1.name as owner, dv1.value as source, cu.name as customer_id, co.fullname from tbl_contacts co join tbl_user u1 on co.owner=u1.id left join tbl_dic_value dv1 on co.source=dv1.id join tbl_customer cu on cu.id = co.customer_id
and u1.name like '%' #{owner} '%'
and co.fullname like '%' #{fullname} '%'
and cu.name like '%' #{customerId} '%'
and dv1.id=#{source}
order by co.create_time desc limit #{beginNo},#{pageSize}

mapper.delete:
delete from tbl_contacts
where id in

    #{id}
