mapper.select:
select t.id, u1.name as owner, t.name, cu.name as customer_id, dv1.value as stage, dv3.value as type, dv2.value as source, co.fullname as contacts_id from tbl_tran t join tbl_user u1 on t.owner=u1.id join tbl_customer cu on t.customer_id=cu.id join tbl_dic_value dv1 on t.stage=dv1.id left join tbl_dic_value dv2 on t.source=dv2.id left join tbl_dic_value dv3 on t.type=dv3.id left join tbl_contacts co on t.contacts_id=co.id
and u1.name like '%' #{owner} '%'
and t.name like '%' #{name} '%'
and cu.name like '%' #{customerId} '%'
and dv1.id=#{stage}
and dv3.id=#{type}
and dv2.id=#{source}
and co.fullname like '%' #{contactsId} '%'
order by t.create_time desc limit #{beginNo},#{pageSize}

select count(*) from tbl_tran t join tbl_user u1 on t.owner=u1.id join tbl_customer cu on t.customer_id=cu.id join tbl_dic_value dv1 on t.stage=dv1.id left join tbl_dic_value dv2 on t.source=dv2.id left join tbl_dic_value dv3 on t.type=dv3.id left join tbl_contacts co on t.contacts_id=co.id
and u1.name like '%' #{owner} '%'
and t.name like '%' #{name} '%'
and cu.name like '%' #{customerId} '%'
and dv1.id=#{stage}
and dv3.id=#{type}
and dv2.id=#{source}
and co.fullname like '%' #{contactsId} '%'
